sys_ui_page.client_script:
try {
    gel('response').innerHTML = 'Loading...';
    var ga = new GlideAjax('GetAllPlugins');
    ga.addParam('sysparm_name', 'getAllPlugins');
    ga.getXML(callBack);

    function callBack(response) {
        try {
            var answer = response.responseXML.documentElement.getAttribute("answer");
            var plugins = JSON.parse(answer);
            var lobList = document.createElement("ul");
            var lobs = groupByLob(plugins);
            for (var lob in lobs) {
                var lobItem = document.createElement("li");
                lobItem.innerText = lob;
                var apps = lobs[lob];
                var appList = document.createElement("ul");
                apps.forEach(function(app) {
                    var appItem = document.createElement("li");
                    var packageSysID = app.package_sys_id;
                    var scopeSysID = app.scope_sys_id;
                    var storeAppSysID = app.store_app_sys_id;
                    if (app.active == '1' && (storeAppSysID || scopeSysID || packageSysID)) {
                        var appLink = document.createElement("a");
                        var sysID;
                        if (storeAppSysID)
                            sysID = storeAppSysID;
                        else if (scopeSysID)
                            sysID = scopeSysID;
                        else
                            sysID = packageSysID;
                        appLink.href = "https://" + app.instance_name + ".service-now.com/sys_package.do?sys_id=" + sysID;
                        appLink.target = '_blank';
                        appLink.innerText = app.app_name + ' âžš';
                        appItem.appendChild(appLink);
                    } else {
                        appItem.innerText = app.name;
                    }
                    appList.appendChild(appItem);
                });
                lobItem.appendChild(appList);
                lobList.appendChild(lobItem);
            }
            gel('response').innerHTML = '';
            gel('response').appendChild(lobList);
        } catch (ex) {
            var message = 'An exception occured while parsing data. Please contact system administrator. Exception: ' + ex;
            gel('response').innerHTML = message;
        }

    }


    function groupByLob(plugins) {
        return plugins.reduce(function(r, a) {
            r[a.lob_name] = r[a.lob_name] || [];
            r[a.lob_name].push(a);
            return r;
        }, Object.create(null));
    }
} catch (ex) {
    var message = 'An exception occured while fecthing data. Please contact system administrator. Exception: ' + ex;
    gel('response').innerHTML = message;
}
